// src/components/Header.js
import React from 'react';
import styled from 'styled-components';
import { Link } from 'react-router-dom';

const NavContainer = styled.nav`
  background-color: purple;
  padding: 10px;
  display: flex;
  justify-content: space-between;
`;

const NavItem = styled.div`
  color: white;
  font-weight: bold;
  text-transform: uppercase;
  cursor: pointer;
  margin-left: 10px;

  &:hover {
    color: #ffcc00;
  }
`;

const Header = () => {
  return (
    <NavContainer>
      <Link to="/" style={{ textDecoration: 'none' }}>
        <NavItem>UMC MOVIE</NavItem>
      </Link>
      <div style={{ display: 'flex' }}>
        <Link to="/popular" style={{ textDecoration: 'none' }}>
          <NavItem>Popular</NavItem>
        </Link>
        <Link to="/nowplaying" style={{ textDecoration: 'none' }}>
          <NavItem>Now Playing</NavItem>
        </Link>
        <Link to="/toprated" style={{ textDecoration: 'none' }}>
          <NavItem>Top Rated</NavItem>
        </Link>
        <Link to="/upcoming" style={{ textDecoration: 'none' }}>
          <NavItem>Upcoming</NavItem>
        </Link>
      </div>
    </NavContainer>
  );
};

export default Header;
