import React from 'react';
import styled from 'styled-components';

const Card = styled.div`
  background-color: #fff;
  border-radius: 8px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  overflow: hidden;
  transition: transform 0.2s ease;
  width: calc(25% - 16px); /* Adjust width to fit four cards in a row */
  margin: 0 8px 16px; /* Add some margin between cards */
  display: inline-block; /* Display cards inline */
  position: relative; /* Position for hover effect */

  &:hover {
    transform: translateY(-4px);
  }
`;

const Image = styled.img`
  width: 100%;
  height: auto;
`;

const Content = styled.div`
  position: absolute;
  bottom: 0;
  left: 0;
  width: 100%;
  background-color: rgba(0, 0, 0, 0.8);
  color: white;
  padding: 16px;
  box-sizing: border-box;
  border-radius: 0 0 8px 8px;
  opacity: ${({ isHovered }) => (isHovered ? '1' : '0')}; /* Show content only when hovered */
  transition: opacity 0.3s ease;
`;

const Title = styled.h2`
  font-size: 18px;
  margin-bottom: 8px;
`;

const Rating = styled.span`
  font-size: 14px;
`;

const Overview = styled.p`
  font-size: 14px;
`;

const MovieCard = ({ movie }) => {
  const { title, overview, poster_path, vote_average } = movie;
  const IMG_BASE_URL = 'https://image.tmdb.org/t/p/w500';
  const [isHovered, setIsHovered] = React.useState(false);

  return (
    <Card onMouseEnter={() => setIsHovered(true)} onMouseLeave={() => setIsHovered(false)}>
      <Image src={IMG_BASE_URL + poster_path} alt="Movie Poster" />
      <Content isHovered={isHovered}>
        <Title>{title}</Title>
        <Rating>평점: {vote_average}</Rating>
        <Overview>{overview}</Overview>
      </Content>
    </Card>
  );
};

export default MovieCard;
